import React, {useEffect, useState} from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Link as ScrollLink } from 'react-scroll';
export default function Navbar(){ const [dark,setDark]=useState(false); const location=useLocation(); useEffect(()=>{ if(dark) document.documentElement.classList.add('dark'); else document.documentElement.classList.remove('dark'); },[dark]); const onHome = location.pathname === '/'; return (<header className='bg-white dark:bg-gray-800 shadow sticky top-0 z-40'><div className='max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 flex items-center justify-between h-16'><div><Link to='/' className='text-xl font-bold text-blue-600'>Selva Adhityas</Link></div><nav className='flex items-center space-x-4'>{onHome? (<><ScrollLink to='about' smooth offset={-60} className='text-gray-700 dark:text-gray-200 cursor-pointer'>About</ScrollLink><ScrollLink to='skills' smooth offset={-60} className='text-gray-700 dark:text-gray-200 cursor-pointer'>Skills</ScrollLink><ScrollLink to='projects' smooth offset={-60} className='text-gray-700 dark:text-gray-200 cursor-pointer'>Projects</ScrollLink><ScrollLink to='resume' smooth offset={-60} className='text-gray-700 dark:text-gray-200 cursor-pointer'>Resume</ScrollLink><ScrollLink to='contact' smooth offset={-60} className='text-gray-700 dark:text-gray-200 cursor-pointer'>Contact</ScrollLink></>) : (<><Link to='/about' className='text-gray-700 dark:text-gray-200'>About</Link><Link to='/skills' className='text-gray-700 dark:text-gray-200'>Skills</Link><Link to='/projects' className='text-gray-700 dark:text-gray-200'>Projects</Link><Link to='/resume' className='text-gray-700 dark:text-gray-200'>Resume</Link><Link to='/contact' className='text-gray-700 dark:text-gray-200'>Contact</Link></>)}<button onClick={()=>setDark(d=>!d)} className='ml-4 p-2 bg-gray-100 dark:bg-gray-700 rounded'>{dark? 'ðŸŒ™':'ðŸŒž'}</button></nav></div></header>); }
